div.script:
                                            (function() {
                                                // Données des tarifs
                                                var bikePrices = {


                                                            : {
                                                                hour: ,
                                                                day: ,
                                                                month: 
                                                            },


                                                };

                                                function calculatePrice() {
                                                    var bikeId = document.getElementById('bike_id').value;
                                                    var startDateInput = document.getElementById('start_date').value;
                                                    var endDateInput = document.getElementById('end_date').value;
                                                    var rentalType = document.getElementById('rental_type').value;
                                                    var priceDiv = document.getElementById('price_display');

                                                    if (!bikeId || !startDateInput || !endDateInput) {
                                                        priceDiv.innerHTML = '
 Sélectionnez un vélo et des dates valides';
                                                        return;
                                                    }

                                                    var startDate = new Date(startDateInput);
                                                    var endDate = new Date(endDateInput);

                                                    if (isNaN(startDate.getTime()) || isNaN(endDate.getTime()) || startDate >= endDate) {
                                                        priceDiv.innerHTML = '
 Les dates ne sont pas valides';
                                                        return;
                                                    }

                                                    var duration = (endDate - startDate) / (1000 * 60 * 60 * 24);
                                                    var prices = bikePrices[bikeId];

                                                    if (!prices) {
                                                        priceDiv.innerHTML = '
 Vélo non trouvé';
                                                        return;
                                                    }

                                                    var unitPrice = prices[rentalType];
                                                    var totalPrice = 0;
                                                    var typeLabel = '';

                                                    if (rentalType === 'hour') {
                                                        totalPrice = unitPrice * (duration * 24);
                                                        typeLabel = 'heure';
                                                    } else if (rentalType === 'day') {
                                                        totalPrice = unitPrice * duration;
                                                        typeLabel = 'jour';
                                                    } else if (rentalType === 'month') {
                                                        totalPrice = unitPrice * (duration / 30);
                                                        typeLabel = 'mois';
                                                    }

                                                    priceDiv.innerHTML = 
                                                        '
' +
                                                        '
 Prix estimé' +
                                                        '
Durée: ' + duration.toFixed(1) + ' jours' +
                                                        '
Tarif unitaire: ' + unitPrice.toFixed(2) + '€/' + typeLabel + '' +
                                                        '
Prix total: ' + totalPrice.toFixed(2) + ' €' +
                                                        '';
                                                }

                                                // Attacher les événements
                                                document.getElementById('bike_id').addEventListener('change', calculatePrice);
                                                document.getElementById('start_date').addEventListener('change', calculatePrice);
                                                document.getElementById('end_date').addEventListener('change', calculatePrice);
                                                document.getElementById('rental_type').addEventListener('change', calculatePrice);
                                            })();
